mapper.select:
SELECT 

FROM oa_common_audit a

WHERE a.id = #{id}

SELECT distinct 


		,r.read_flag AS "readFlag"

FROM oa_common_audit a



		LEFT JOIN oa_common_audit_record r ON r.common_audit_id = a.id


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.current_by = #{currentBy.id}


		AND a.status = #{status}


		AND a.office_id = #{office.id}


		AND a.create_by = #{createBy.id}


		AND a.create_by = #{createId}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}



		AND r.user_id = #{currentUser.id}


		AND r.deal_type='1' 


		AND (a.title LIKE 
			'%'||#{keywords}||'%'
			'%'+#{keywords}+'%'
			concat('%',#{keywords},'%')
			 OR 
			 u8.name LIKE 
			'%'||#{keywords}||'%'
			'%'+#{keywords}+'%'
			concat('%',#{keywords},'%')
			)



${sqlMap.dsf} 


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_common_audit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_common_audit SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
