mapper.select:
SELECT 

FROM oa_common_flow a

WHERE a.id = #{id}

SELECT 

FROM oa_common_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.status = #{status}



${sqlMap.dsf} 


		ORDER BY ${page.orderBy}


		ORDER BY a.type ASC

SELECT 

FROM oa_common_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}


${sqlMap.dsf} 	


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_common_flow SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
