mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.login_name = #{loginName} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM sys_user a
JOIN sys_user_role ur ON ur.user_id = a.id AND ur.role_id = #{role.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND (c.id = #{company.id} OR c.parent_ids LIKE '%,'||#{company.id}||',%') '%'+#{company.id}+'%') CONCAT('%,', #{company.id}, ',%'))
AND (o.id = #{office.id} OR o.parent_ids LIKE '%,'||#{office.id}||',%') '%'+#{office.id}+'%') CONCAT('%,', #{office.id}, ',%'))
AND a.id != '1'
AND a.login_name = #{loginName}
AND a.name like '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
AND a.login_flag = #{loginFlag}
AND a.bind_wx = #{bindWx}
AND a.user_id = #{userId}
AND a.mobile like '%'||#{mobile}||'%' '%'+#{mobile}+'%' CONCAT('%', #{mobile}, '%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY c.code, o.code, a.name

SELECT
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND c.id = #{company.id}
AND o.id = #{office.id}
AND (o.id = '' or o.id is null)
AND a.login_name like '%'||#{loginName}||'%' '%'+#{loginName}+'%' CONCAT('%', #{loginName}, '%')
AND a.name like '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
${sqlMap.dsf}
ORDER BY a.name

SELECT
	a.id, a.name, a.login_name, a.photo 
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id = #{office.id}
ORDER BY a.name

SELECT

FROM sys_user a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

${sqlMap.dsf} 
ORDER BY c.code, o.code, a.name

mapper.update:
UPDATE sys_user SET 
	password = #{password} 
WHERE id = #{id}

UPDATE sys_user SET 
	login_ip = #{loginIp}, 
	login_Date = #{loginDate},
	token = #{token} 
WHERE id = #{id}

UPDATE sys_user SET  
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}, 
	login_flag = #{loginFlag}
WHERE id = #{id}

UPDATE sys_user SET 
	data_scope = #{dataScope} 
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.insert:
INSERT INTO sys_user_friend(
	id, 
	userId, 
	friendId
) VALUES (
	#{id}, 
	#{userId}, 
	#{friendId}
)

mapper.select:
SELECT
FROM sys_user a
LEFT JOIN sys_user_friend p ON p.friendId = a.id WHERE p.userId = #{id}

SELECT
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
${sqlMap.dsf}
AND a.name like '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')

SELECT

FROM sys_user a

WHERE a.account_id = #{accountId} AND a.user_id = #{userId} AND a.del_flag = #{DEL_FLAG_NORMAL}
