mapper.select:
SELECT 

FROM oa_project a

WHERE a.id = #{id}

SELECT 

FROM oa_project a



	JOIN oa_project_record r ON r.oa_project_id = a.id AND r.user_id = #{currentUser.id}

		AND r.read_flag = #{readFlag}



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.no = #{no}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.own_by = #{ownBy.id}


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.end_date BETWEEN #{beginEndDate} AND #{endEndDate}


		AND a.status = #{status}


		AND a.status != 2 AND a.status != 3




${sqlMap.dsf} 


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_project a


	a.del_flag = #{DEL_FLAG_NORMAL}


${sqlMap.dsf} 		


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_project SET
	schedule = #{schedule}
WHERE id = #{projId}

UPDATE oa_project SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(0) AS count FROM oa_project a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.own_by = #{ownBy.id}

SELECT COUNT(0) AS count FROM oa_project a

	a.del_flag = #{DEL_FLAG_NORMAL}
		AND a.status = #{status}
